let navbar=document.querySelector("nav");window.addEventListener("scroll",e=>{window.scrollY>100?navbar.classList.add("active"):navbar.classList.remove("active")});const links=document.querySelectorAll("nav ul li");links.forEach(e=>{e.addEventListener("click",()=>{document.querySelector("nav ul li.active").classList.remove("active"),e.classList.add("active"),document.querySelector(e.dataset.section.toString()).scrollIntoView({behavior:"smooth"})})}),document.querySelector("nav .logo").addEventListener("click",e=>{document.querySelector("header").scrollIntoView({behavior:"smooth"})});let exploreBtn=document.querySelector("header .title .btn");exploreBtn.addEventListener("click",function(){document.querySelector(".hadith").scrollIntoView({behavior:"smooth"})});const hadithContainer=document.querySelector(".hadith .hadithContainer"),prevBtn=document.querySelector(".hadith .buttons .prev"),nextBtn=document.querySelector(".hadith .buttons .next"),numbers=document.querySelector(".hadith .buttons .numbers");let hadithIndex=0;function hadithChanger(){fetch("https://api.hadith.sutanlab.id/books/muslim?range=1-300").then(e=>e.json()).then(e=>{let t=e.data.hadiths;hadithWrite(t),prevBtn.addEventListener("click",function(){0==hadithIndex?hadithIndex=299:hadithIndex--,hadithWrite(t)}),nextBtn.addEventListener("click",function(){299==hadithIndex?hadithIndex=0:hadithIndex++,hadithWrite(t)})})}function hadithWrite(e){hadithContainer.innerText=e[hadithIndex].arab,numbers.innerText=`300 - ${hadithIndex+1}`}hadithChanger();let surahsContainer=document.querySelector(".quran .surahs-container");function getSurahs(){surahsContainer.innerHTML="",fetch("http://api.alquran.cloud/v1/meta").then(e=>e.json()).then(e=>{let t=e.data.surahs.references,n=e.data.surahs.count;for(let e=0;e<n;e++)surahsContainer.innerHTML+=`\n        <div class="surah">\n          <p>${t[e].name}</p>\n          <p>${t[e].englishName}</p>\n        </div>\n      `;let a=document.querySelectorAll(".quran .surah");const r=document.querySelector(".surahs-popup"),i=document.querySelector(".surahs-popup .close-popup"),c=document.querySelector(".surahs-popup .ayats");a.forEach((e,t)=>{e.addEventListener("click",()=>{fetch(`https://api.alquran.cloud/v1/surah/${t+1}`).then(e=>e.json()).then(e=>{c.innerHTML="",e.data.ayahs.forEach(e=>{r.classList.add("active"),c.innerHTML+=`\n              <p>${e.text} - {${e.numberInSurah}}</p>\n            `})})})}),i.addEventListener("click",()=>{r.classList.remove("active")})})}getSurahs();let cards=document.querySelector(".pray .cards");function getPrayTime(){fetch("https://api.aladhan.com/v1/timingsByCity?city=Ghardaia&country=Algeria&method=8").then(e=>e.json()).then(e=>{let t=e.data.timings;cards.innerHTML="";for(let e in t)cards.innerHTML+=`\n        <div class="card">\n            <div class="circle"><svg>\n                    <circle cx="100" cy="100" r="100"></circle>\n                </svg>\n                <div class="pray-time">${t[e]}</div>\n            </div>\n            <p>${e}</p>\n        </div>\n      `})}getPrayTime();