let navbar=document.querySelector("nav");window.addEventListener("scroll",t=>{window.scrollY>100?navbar.classList.add("active"):navbar.classList.remove("active")});let exploreBtn=document.querySelector("header .title .btn");exploreBtn.addEventListener("click",function(){document.querySelector(".hadith").scrollIntoView({behavior:"smooth"})});const hadithContainer=document.querySelector(".hadith .hadithContainer"),prevBtn=document.querySelector(".hadith .buttons .prev"),nextBtn=document.querySelector(".hadith .buttons .next"),numbers=document.querySelector(".hadith .buttons .numbers");let hadithIndex=0;function hadithChanger(){fetch("https://api.hadith.sutanlab.id/books/muslim?range=1-300").then(t=>t.json()).then(t=>{let e=t.data.hadiths;hadithWrite(e),prevBtn.addEventListener("click",function(){0==hadithIndex?hadithIndex=299:hadithIndex--,hadithWrite(e)}),nextBtn.addEventListener("click",function(){299==hadithIndex?hadithIndex=0:hadithIndex++,hadithWrite(e)})})}function hadithWrite(t){hadithContainer.innerText=t[hadithIndex].arab,numbers.innerText=`300 - ${hadithIndex+1}`}hadithChanger();